function renderProductView() {
    var container = document.getElementById("main");
    if (!container)
        return;
    container.innerHTML = "\n          <div class=\"product-view\">\n    <div class=\"product-gallery\">\n      <div class=\"main-image\">\n        <img src=\"https://via.placeholder.com/500\" alt=\"Main Product Image\">\n      </div>\n      <div class=\"thumbnail-images\">\n        <img src=\"https://via.placeholder.com/100\" alt=\"Thumbnail 1\">\n        <img src=\"https://via.placeholder.com/100\" alt=\"Thumbnail 2\">\n        <img src=\"https://via.placeholder.com/100\" alt=\"Thumbnail 3\">\n      </div>\n    </div>\n\n    <div class=\"product-details\">\n      <h1 class=\"product-title\">Product Name</h1>\n      <p class=\"product-price\">$99.99</p>\n      <p class=\"product-description\">\n        This is a short description of the product highlighting its features and benefits.\n      </p>\n\n      <div class=\"product-options\">\n        <label for=\"size\">Size:</label>\n        <select id=\"size\">\n          <option value=\"small\">Small</option>\n          <option value=\"medium\">Medium</option>\n          <option value=\"large\">Large</option>\n        </select>\n\n        <label for=\"quantity\">Quantity:</label>\n        <input type=\"number\" id=\"quantity\" value=\"1\" min=\"1\">\n      </div>\n\n      <button class=\"add-to-cart\">Add to Cart</button>\n    </div>\n  </div>\n    ";
}
